import React, { useContext, useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { CartContext } from '../context/CartContext';
import Toast from './Toast'; // Import the Toast component

const LayoutHeader = ({ onCartClick }) => {
  const { cartItems, showToast } = useContext(CartContext); // showToast to trigger animation
  const itemCount = cartItems.reduce((sum, item) => sum + item.quantity, 0);
  const [animateCartIcon, setAnimateCartIcon] = useState(false);

  useEffect(() => {
    if (showToast) { // Using showToast as a trigger, assuming it becomes true when an item is added
      setAnimateCartIcon(true);
      const timer = setTimeout(() => {
        setAnimateCartIcon(false);
      }, 500); // Animation duration
      return () => clearTimeout(timer);
    }
  }, [showToast, cartItems]); // Depend on cartItems as well to re-trigger if same item added again quickly

  return (
    <>
      <header className="sticky top-0 z-50 bg-white shadow-sm">
        <div className="container mx-auto px-4 py-4 flex justify-between items-center">
          <Link to="/" className="text-2xl font-bold text-gray-800">SmartShop</Link>
          <nav className="hidden md:flex space-x-8">
            <Link to="/inicio" className="text-gray-600 hover:text-black">Inicio</Link>
            <Link to="/tienda" className="text-gray-600 hover:text-black">Tienda</Link>
            <Link to="/nosotros" className="text-gray-600 hover:text-black">Nosotros</Link>
            <Link to="/contacto" className="text-gray-600 hover:text-black">Contacto</Link>
          </nav>
          <div className="flex items-center space-x-4">
            <button
              onClick={onCartClick}
              className={`relative p-2 rounded-full hover:bg-gray-100 ${animateCartIcon ? 'animate-cart-icon-shake' : ''}`}
            >
              <svg className="w-6 h-6 text-gray-600" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M16 11V7a4 4 0 00-8 0v4M5 9h14l1 12H4L5 9z"></path>
              </svg>
              {itemCount > 0 && (
                <span className="absolute top-0 right-0 bg-red-500 text-white text-xs rounded-full h-5 w-5 flex items-center justify-center">
                  {itemCount}
                </span>
              )}
            </button>
            <button className="md:hidden p-2 rounded-full hover:bg-gray-100">
              <svg className="w-6 h-6 text-gray-600" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M4 6h16M4 12h16M4 18h16"></path>
              </svg>
            </button>
          </div>
        </div>
      </header>
      <Toast />
    </>
  );
};

export default LayoutHeader;
